mapper.select:
       	SELECT * FROM COMMON_CODE
		WHERE 1 = 1

        and ${searchType} regexp #{search}

		;

mapper.update:
update COMMON_CODE
set CODE_NAME = #{CODE_NAME}
where 1 = 1
and PK_UNIQUE = #{PK_UNIQUE};

mapper.delete:
delete from COMMON_CODE
where 1 = 1
and PK_UNIQUE = #{PK_UNIQUE};
